#!/bin/bash

# Rural Hardware Store Setup Script
echo "Setting up Rural Hardware Store application..."

# Create project directory if it doesn't exist
mkdir -p /Users/dineshkumargopalakrishnan/RetailApp
cd /Users/dineshkumargopalakrishnan/RetailApp

# Clone the repository
echo "Cloning repository..."
git clone https://github.com/dineshgin/rural-hardware-store.git .

# Install dependencies
echo "Installing dependencies..."
npm install

# Create necessary directories
echo "Creating directories..."
mkdir -p src/database
mkdir -p src/styles
mkdir -p assets/icons

# Create a simple icon for the application
echo "Creating application icon..."
cat > assets/icons/icon.png << EOL

EOL

echo "Setup complete! You can now run the application with 'npm start'"